import React, { useEffect, useState } from "react";
import axios from "axios";
import CharCardComp from "./CharCardComp";
import Find from "../src/components/FindComponent/Find";
import styled from "styled-components";
import { Button } from "reactstrap";

import { Container, Row, Col } from "reactstrap";

export default function CharCardList(props) {
  const [char, setChar] = useState([]);
  const [showContent, setShowContent] = useState(false);

  useEffect(() => {
    axios
      .get("https://swapi.co/api/people")
      .then(result => {
        setChar(result.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  const Wrapper = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    margin: 2%;
  `;

  function toggleContentHandler() {
    const doesShow = showContent;
    setShowContent(!doesShow);
  }

  return (
    <div>
      <Wrapper>
        <Button onClick={toggleContentHandler}>Explore the Characters</Button>
        <Find />
      </Wrapper>
      <Container>
        <Row>
          {char.map(character => {
            return (
              <Col xs="6">
                <CharCardComp
                  //   key={character.id}
                  name={character.name}
                  birthyear={character.birth_year}
                  home={character.home}
                  eye={character.eye_color}
                />
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
}
