import React, { useEffect, useState } from "react";
import { Form, FormGroup, Label, Input, DropdownItem } from "reactstrap";
import axios from "axios";
// import CardComp from "../CardComponents/CardComp";

export default function FindChar(props) {
  const [selectFilm, setSelectFilm] = useState(props);
  const [char, setChar] = useState([]);

  useEffect(() => {
    axios
      .get("https://swapi.co/api/people")
      .then(result => {
        setChar(result.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  function selectCharHandler() {
    const doesShow = selectFilm;
    setSelectFilm(!doesShow);
  }
  console.log(char);

  return (
    <div>
      {char.map(name => {
        return <DropdownItem key={name.index}>{name.name}</DropdownItem>;
      })}
    </div>
  );
}

