import React, { useState } from "react";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
import CharCardList from "./components/CharCardComponents/CharCardList";
import MovCardList from "./components/MovCardComponent/MovieCardList";
import Find from "../FindComponent/Find";

export default function NavBar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };
  const [showContent, setShowContent] = useState(false);

  // const Wrapper = styled.div`
  //   display: flex;
  //   justify-content: space-around;
  //   align-items: center;
  //   margin: 2%;
  // `;

  function toggleContentHandler() {
    const doesShow = showContent;
    setShowContent(!doesShow);
  }
  //   <Wrapper>
  //   <Button onClick={toggleContentHandler}>Explore the Characters</Button>
  //   <Find />
  // </Wrapper>

  return (
    <div>
      <div>
        <Navbar color="light" light expand="md">
          <NavbarBrand href="/">ReactWars</NavbarBrand>
          <NavbarToggler onClick={toggle} />
          <Collapse isOpen={isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink onClick={toggleContentHandler}>
                  Explore the Characters
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">
                  GitHub
                </NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret>
                  Options
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>Option 1</DropdownItem>
                  <DropdownItem>Option 2</DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem>Reset</DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
      <div>
        {showContent ? (
          <div>
            <CharCardList />
            <MovCardList />
          </div>
        ) : null}
      </div>
    </div>
  );
}
