import React, { useEffect, useState } from "react";
import { DropdownItem } from "reactstrap";
import axios from "axios";
import { Card, CardText, CardBody, CardTitle, CardSubtitle } from "reactstrap";
// import CardComp from "../CardComponents/CardComp";

export default function FindChar(props) {
  // const [selectFilm, setSelectFilm] = useState(props);
  const [char, setChar] = useState([]);

  useEffect(() => {
    axios
      .get("https://swapi.co/api/people")
      .then(result => {
        setChar(result.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  function selectCharHandler() {
    console.log(char.name + "from the select handler");
    return (
      <Card>
        <CardBody>
          <CardTitle>{char.name}</CardTitle>
          <CardSubtitle>
            Born in the great year of {char.birthyear}
          </CardSubtitle>
          <CardText>
            You don't know fear until you have stared into my steely {char.eye}{" "}
            eyes!
          </CardText>
        </CardBody>
      </Card>
    );
  }
  // console.log(char);

  return (
    <div>
      {char.map((name, index) => {
        return (
          <DropdownItem onClick={selectCharHandler} key={index}>
            {name.name}
          </DropdownItem>
        );
      })}
    </div>
  );
}
