import React, { useEffect, useState } from "react";
import { DropdownItem } from "reactstrap";
import axios from "axios";
import CharCardList from "../CharCardComponents/CharCardList";

export default function FindChar(props) {
  // const [selectFilm, setSelectFilm] = useState(props);
  const [char, setChar] = useState([]);
  console.log(props);
  console.log(CharCardList);
  useEffect(() => {
    axios
      .get("https://swapi.co/api/people")
      .then(result => {
        setChar(result.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  function selectCharHandler() {
    // const doesShow = selectFilm;
    // setSelectFilm(!doesShow);
  }
  console.log(char);

  return (
    <div>
      {char.map(name => {
        return (
          <DropdownItem onClick={selectCharHandler} key={name.index}>
            {name.name}
          </DropdownItem>
        );
      })}
    </div>
  );
}
