import React, { useState } from "react";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";
import { Container, Row, Col } from "reactstrap";
import styled from "styled-components";

const CardComp = ({ title, description, id, director }) => {
  const Wrapper = styled.div`
    display: flex;
    justify-content: center;
    align-conent: center;
    margin: 2%;
  `;
  return (
    <Container>
      <Row>
        <Col xs="6">
          <Card key={id}>
            <CardBody>
              <CardTitle>{title}</CardTitle>
              <CardSubtitle>{director}</CardSubtitle>
              <CardText>{description}</CardText>
              <Button>Next</Button>
            </CardBody>
          </Card>
        </Col>
        <Col xs="6">
          <Wrapper>
            <CardImg
              center
              width="100%"
              src="../images/starwars.jpeg"
              alt="Card image cap"
            />
          </Wrapper>
        </Col>
      </Row>
    </Container>
  );
};

export default CardComp;
