import React, { useState, useEffect } from "react";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";
import { Container, Row, Col } from "reactstrap";
import styled from "styled-components";
import axios from "axios";
const CardComp = ({ name, home, birthyear, eye }) => {
  const Wrapper = styled.div`
    display: flex;
    justify-content: center;
    align-conent: center;
    margin: 2%;
  `;

  //   useEffect(() => {
  //     axios
  //       .get("https://swapi.co/api/people")
  //       .then(result => {
  //         setChar(result.data.results);
  //       })
  //       .catch(err => {
  //         console.log(err);
  //       });
  //   }, []);
  const [filmTitle, setFilmTitle] = useState([]);
  //   const eachFilm = [];
  //   films.forEach(film => {
  //     return film.title;
  //   });

  //   console.log(eachFilm);
  useEffect(() => {
    axios
      .get("https://swapi.co/api/films/")
      .then(result => {
        setFilmTitle(result.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);
  console.log(filmTitle);

  return (
    <Container>
      <Row>
        <Col xs="6">
          <Card>
            <CardBody>
              <CardTitle>{name}</CardTitle>
              <CardSubtitle>Born in the great year of {birthyear}</CardSubtitle>
              <CardText>
                You don't know fear until you have stared into my steely {eye}{" "}
                eyes!
              </CardText>
              <Button>Next</Button>
            </CardBody>
          </Card>
        </Col>
        <Col xs="6">
          {/* <Wrapper>
            <CardImg
              center
              width="100%"
              src="../images/starwars.jpeg"
              alt="Card image cap"
            />
          </Wrapper> */}
        </Col>
      </Row>
    </Container>
  );
};

export default CardComp;
