import React, { useState } from "react";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";
import { Container, Row, Col } from "reactstrap";
import styled from "styled-components";

const CardComp = ({ name, home, films }) => {
  const Wrapper = styled.div`
    display: flex;
    justify-content: center;
    align-conent: center;
    margin: 2%;
  `;
  const eachFilm = [];
  films.forEach(film => {
    eachFilm.push(film);
  });
  console.log(films);
  return (
    <Container>
      <Row>
        <Col xs="6">
          <Card>
            <CardBody>
              <CardTitle>{name}</CardTitle>
              <CardSubtitle>{home}</CardSubtitle>
              <CardText>{eachFilm}</CardText>
              <Button>Next</Button>
            </CardBody>
          </Card>
        </Col>
        <Col xs="6">
          {/* <Wrapper>
            <CardImg
              center
              width="100%"
              src="../images/starwars.jpeg"
              alt="Card image cap"
            />
          </Wrapper> */}
        </Col>
      </Row>
    </Container>
  );
};

export default CardComp;
