import React, { useState } from "react";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
import CharCardList from "../CharCardComponents/CharCardList";
import MovCardList from "../MovCardComponent/MovieCardList";
import FindChar from "../FindComponent/FindChar";
import FindMov from "../FindComponent/FindMov";
import styled from "styled-components";

export default function NavBar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };
  const [showCharContent, setShowCharContent] = useState(false);
  const [showMovContent, setShowMovContent] = useState(false);

  const Wrapper = styled.div`
    paddingtop: 56.25%;
    margin: 5%;
  `;

  function toggleCharContentHandler() {
    const doesCharShow = showCharContent;
    setShowCharContent(!doesCharShow);
  }
  function toggleMovContentHandler() {
    const doesMovShow = showMovContent;
    setShowMovContent(!doesMovShow);
  }

  return (
    <div>
      <div>
        <Navbar color="light" light expand="md">
          <NavbarBrand href="/">ReactWars</NavbarBrand>
          <NavbarToggler onClick={toggle} />
          <Collapse isOpen={isOpen} navbar>
            <Nav className="ml-auto" navbar>
              <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret onClick={toggleCharContentHandler}>
                  Characters
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>Characters</DropdownItem>
                  <FindChar />
                  <DropdownItem divider />
                  <DropdownItem onClick={toggleCharContentHandler}>
                    Show All
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
              <UncontrolledDropdown>
                <DropdownToggle nav caret onClick={toggleMovContentHandler}>
                  Movies
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>Movies</DropdownItem>
                  <FindMov />
                  <DropdownItem divider />
                  <DropdownItem onClick={toggleMovContentHandler}>
                    Show All
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
      <div>
        <Wrapper>
          {showCharContent ? (
            <Wrapper>
              <div>
                <CharCardList />
              </div>
            </Wrapper>
          ) : null}
          {showMovContent ? (
            <div>
              <MovCardList />
            </div>
          ) : null}
        </Wrapper>
      </div>
    </div>
  );
}
